import axios from "axios";
import React from "react";
import { Button, FlatList, TextInput, View } from "react-native";
 
class Search extends React.Component {
  constructor(props){
    super(props)

    this.state = {
      getPostal: '',
    }
  }

  postPostalCodeSearch() {
    // this.setState(state => ({
    //   isToggleOn: !state.isToggleOn
    // }));
       axios
      .get(
        '  http://api.meteo-concept.com/api/location/city?token=beff0b2665689f298e408220eda60e6d3febb2a72b1e16cca5d1e2ec39df9f14&insee=' +
          // this.setState(state => ({
          //   getPostal: ''
          // }))
          this.state.getPostal
      )
      .then(function (response) {
        alert(
          'Weather codes Temps sensible cette nuit à Strasbourg : ' +
            JSON.stringify(response.data.forecast)
        )
      })
  }
 
  render(){
    return(
    <View>
      <TextInput
        placeholder="Code postal"
        value={this.state.getPostal}
        onChange={e => this.setState({getPostal: e.target.value})}
      />

      <Button title="Rechercher" onPress={this.postPostalCodeSearch}/>
      <FlatList  />
    </View>
  )}
}

export default Search
